<?php
class Twig_Profiler_Dumper_Html extends Twig_Profiler_Dumper_Text { private static $colors = array('block' => '#dfd', 'macro' => '#ddf', 'template' => '#ffd', 'big' => '#d44'); public function dump(Twig_Profiler_Profile $sp6e7f73) { return '<pre>' . parent::dump($sp6e7f73) . '</pre>'; } protected function formatTemplate(Twig_Profiler_Profile $sp6e7f73, $sp754d6b) { return sprintf('%s└ <span style="background-color: %s">%s</span>', $sp754d6b, self::$colors['template'], $sp6e7f73->getTemplate()); } protected function formatNonTemplate(Twig_Profiler_Profile $sp6e7f73, $sp754d6b) { return sprintf('%s└ %s::%s(<span style="background-color: %s">%s</span>)', $sp754d6b, $sp6e7f73->getTemplate(), $sp6e7f73->getType(), isset(self::$colors[$sp6e7f73->getType()]) ? self::$colors[$sp6e7f73->getType()] : 'auto', $sp6e7f73->getName()); } protected function formatTime(Twig_Profiler_Profile $sp6e7f73, $sp98286e) { return sprintf('<span style="color: %s">%.2fms/%.0f%%</span>', $sp98286e > 20 ? self::$colors['big'] : 'auto', $sp6e7f73->getDuration() * 1000, $sp98286e); } }