<?php
class Twig_Tests_NativeExtensionTest extends PHPUnit_Framework_TestCase { public function testGetProperties() { if (defined('HHVM_VERSION')) { $this->markTestSkipped('Skip under HHVM as the behavior is not the same as plain PHP (which is an edge case anyway)'); } $speae043 = new Twig_Environment(new Twig_Loader_Array(array('index' => '{{ d1.date }}{{ d2.date }}')), array('debug' => true, 'cache' => false, 'autoescape' => false)); $spf52b2c = new DateTime(); $sp8e612b = new DateTime(); $sp35fa7e = $speae043->render('index', compact('d1', 'd2')); $this->assertEquals($sp35fa7e, $spf52b2c->date . $sp8e612b->date); } }