class Twig_Tests_Node_Expression_ConditionalTest extends Twig_Test_NodeTestCase { public function testConstructor() { $sp01608e = new Twig_Node_Expression_Constant(1, 1); $spe3703d = new Twig_Node_Expression_Constant(2, 1); $spf2a2ea = new Twig_Node_Expression_Constant(3, 1); $spcefb62 = new Twig_Node_Expression_Conditional($sp01608e, $spe3703d, $spf2a2ea, 1); $this->assertEquals($sp01608e, $spcefb62->getNode('expr1')); $this->assertEquals($spe3703d, $spcefb62->getNode('expr2')); $this->assertEquals($spf2a2ea, $spcefb62->getNode('expr3')); } public function getTests() { $sp754928 = array(); $sp01608e = new Twig_Node_Expression_Constant(1, 1); $spe3703d = new Twig_Node_Expression_Constant(2, 1); $spf2a2ea = new Twig_Node_Expression_Constant(3, 1); $spcefb62 = new Twig_Node_Expression_Conditional($sp01608e, $spe3703d, $spf2a2ea, 1); $sp754928[] = array($spcefb62, '((1) ? (2) : (3))'); return $sp754928; } }